/**
 * declarations for importing various asset types in TypeScript projects.
 * These declarations allow TypeScript to understand imports of non-code files,
 * enabling type checking and module resolution for assets like images, stylesheets, and fonts.
 * 4yasenime, superti4r
 */

declare module '*.css';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.png';
declare module '*.svg';
declare module '*.gif';
declare module '*.webp';
declare module '*.mp4';
declare module '*.mp3';
declare module '*.woff';
declare module '*.woff2';
declare module '*.ttf';
declare module '*.eot';
declare module '*.otf';